 <script>
 function generateQRCode() {
        // Lấy dữ liệu từ người dùng hoặc có thể chọn cách khác
        var qrText = ""Mai Anh Lộc"";

        // Gửi yêu cầu đến server để tạo QR code
        $.ajax({
            type: ""POST"",
            url: ""/QRCode/GenerateQRCode"",
            data: { qrText: qrText },
            success: function (base64Image) {
                console.log(base64Image)
                // Hiển thị QR code
                $(""#qrCodeContainer"").html('<img src=""data:image/png;base64,' + base64Image + '"" alt=""QR Code"">');
